import React, { Component } from "react";
// import logo from "./logo.svg";
// import CardList from './components/card-list';
import "./App.css";
import { CardList } from "./components/card-list/card-list.component.jsx";
import { SearchBox } from "./components/search-box/search-box.component.jsx";

class App extends Component {
  constructor() {
    super();
    this.state = {
      list: [],
      searchField: "",
      title: "",
    };

    // this.handleChange = this.handleChange.bind(this); //impt to work for this
  }

  //life-cycle mtd
  componentDidMount() {
    //it return promise
    fetch(
      "https://5w05g4ddb1.execute-api.ap-south-1.amazonaws.com/dev/profile/listAll"
    )
      .then((response) => response.json())
      .then((users) => this.setState({ list: users["list"] }))
      .catch(err => console.log(err));
  }

  handleChange = (e) => {
    this.setState({ searchField: e.target.value, title: e.target.value });
  };

  // set state is asynchronous
  render() {
    const { list, searchField, title } = this.state;
    console.log(list);
    // const filteredMonsters = monsters.filter((monster) =>
    //   monster.name.toLowerCase().includes(searchField.toLowerCase())
    // );
    return (
      <div className="App">
        <h1>{title}</h1>
        <SearchBox
          placeholder="search users"
          handleChange={this.handleChange}
        />

        {/* <CardList monsters={filteredMonsters}></CardList> */}
      </div>
    );
  }
}

export default App;
